            <ul class="navbar-nav">
                <?php
                $navMain = $navbarConfig['main'];
                foreach ($navMain as $label => $path) {
                    $active = false;
                    if ($path === "/") {
                        $active = $request->getPath() === $path;
                    } else {
                        $active = str_starts_with($request->getPath(), $path);
                    }
                    ?>
                    <li class="nav-item">
                        <a class="nav-link <?= $active ? "active" : "" ?>"
                           href="<?= $site->getWebPath() . $path ?>"><?= $label ?></a>
                    </li>
                    <?php
                }
                ?>
